void Update()
{
    if (Input.GetKey(KeyCode.W))
        rb.AddForce(transform.up * thrust);

    float rotate = Input.GetAxis("Horizontal");
    rb.MoveRotation(rb.rotation - rotate * rotateSpeed * Time.deltaTime);
}

void SpawnAsteroid()
{
    Vector3 pos = Random.onUnitSphere * spawnRadius;
    float size = Random.Range(0.5f, 2.5f);

    GameObject ast = Instantiate(asteroidPrefab, pos, Random.rotation);
    ast.transform.localScale = Vector3.one * size;

    Rigidbody rb = ast.GetComponent<Rigidbody>();
    rb.AddForce(Random.onUnitSphere * Random.Range(5,15), ForceMode.Impulse);
}

public void OnSelectEntered(SelectEnterEventArgs args)
{
    grabbed = true;
    rb.isKinematic = true;
}

public void OnSelectExited(SelectExitEventArgs args)
{
    grabbed = false;
    rb.isKinematic = false;
}

void Update()
{
    switch(state)
    {
        case Idle:
            LookForPlayer();
            break;

        case Chase:
            MoveTowardPlayer();
            break;

        case Attack:
            TryShoot();
            break;
    }
}

void Shoot()
{
    Ray ray = new Ray(transform.position, transform.forward);

    if (Physics.Raycast(ray, out var hit, 50f))
    {
        Debug.Log($"Hit {hit.collider.name}");
    }
}

public class StepGuide : MonoBehaviour
{
    public List<TrainingStep> steps;
    private int currentStep = 0;

    void CheckStepCompletion()
    {
        if (steps[currentStep].IsCompleted())
        {
            currentStep++;
            ShowNextInstruction();
        }
    }
}

bool ValidateHandPose(HandPose required)
{
    float similarity = Vector3.Dot(
        currentPose.palmNormal,
        required.palmNormal
    );

    return similarity > 0.95f;
}

void OnTrackedImagesChanged(ARTrackedImagesChangedEventArgs args)
{
    foreach (var trackedImage in args.added)
    {
        string artworkId = trackedImage.referenceImage.name;
        SpawnARContent(artworkId, trackedImage.transform);
    }
}

void HandleTouchInput()
{
    if (Input.touchCount == 2)
    {
        Touch t1 = Input.GetTouch(0);
        Touch t2 = Input.GetTouch(1);

        float pinchDist = Vector2.Distance(t1.position, t2.position);
        float scale = pinchDist / initialPinchDist;

        model.localScale = initialScale * scale;
    }
}
